import React from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 

    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]


  return (
    <div>
      <Course course={courses}/>
    </div>
  )
}

const Course =(props) => {
  let kaka =[]      

  for(let i = 0; i<=props.course.length; i++){
    kaka.push(<Header course = {props.course[i].name}/>)
    kaka.push(<Content parts={props.course[i].parts} />)
    kaka.push(<Total parts={props.course[i].parts} />)

  }
  console.log(kaka)

  return (
    kaka
  )
}

const Header =(props) => {
  return (
    <div>
      <h1>
        {props.course}
      </h1>
    </div>
  )
}

const Content = (props) => {
  return(
    <div>
      <Part part = {props.parts[0]}/>
      <Part part = {props.parts[1]}/>
      <Part part = {props.parts[2]}/>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>
        {props.part.name} {props.part.exercises}
      </p>
    </div>
  )
}
const Total = (props) => {
const total = props.parts.reduce((s,p)=>{
  return s+p.exercises
},0)
  return <p>total of {total} exercises</p>
}

ReactDOM.render(<App />, document.getElementById('root'))
